mapper.select:
SELECT *
FROM tbl_reply
WHERE bno = #{bno}
ORDER BY rno desc
LIMIT #{cri.pageStart}, #{cri.perPageNum}

mapper.update:
UPDATE tbl_reply
SET rcontent = #{rcontent}
WHERE rno = #{rno}

UPDATE tbl_reply
SET state = 'D'
WHERE rno = #{rno}

mapper.select:
SELECT count(bno)
FROM tbl_reply
WHERE bno = #{bno}
